import React, {PropTypes} from 'react';

import ActivityItemBase from '@ciscospark/react-component-activity-item-base';
import ActivityText from '@ciscospark/react-component-activity-text';

export default function ActivityPost(props) {

  const {
    content,
    displayName
  } = props;

  return (
    <ActivityItemBase {...props}>
      <ActivityText content={content} displayName={displayName} />
    </ActivityItemBase>
  );
  /* eslint-enable react/no-danger */
}

ActivityPost.propTypes = {
  content: PropTypes.string,
  displayName: PropTypes.string
};
