import React from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import styles from './styles.css';

export default function ActivityText({content, displayName, renderedComponent}) {
  if (renderedComponent) {
    return (
      <div className={classNames(`ciscospark-activity-text`, styles.activityText)}>
        {renderedComponent}
      </div>
    );
  }
  /* eslint-disable-reason content is considered safe from server */
  /* eslint-disable react/no-danger */

  const htmlContent = {__html: content || displayName};
  return (
    <div
      className={classNames(`ciscospark-activity-text`, styles.activityText)}
      dangerouslySetInnerHTML={htmlContent}
    />
  );

  /* eslint-enable react/no-danger */
}

ActivityText.propTypes = {
  content: PropTypes.string,
  displayName: PropTypes.string,
  renderedComponent: PropTypes.object
};
