import React from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import {ICON_TYPE_DELETE} from '@ciscospark/react-component-icon';
import Button from '@ciscospark/react-component-button';

import styles from './styles.css';

const propTypes = {
  children: PropTypes.node.isRequired,
  id: PropTypes.string.isRequired,
  onRemove: PropTypes.func
};

const defaultProps = {
  onRemove: () => {}
};

function ChipBase(props) {
  const {
    children,
    id,
    onRemove
  } = props;

  function handleRemove() {
    onRemove(id);
  }

  return (
    <div className={classNames('ciscospark-chip', styles.chip)}>
      {children}
      <div className={classNames('ciscospark-chip-action', styles.action)}>
        <Button
          iconType={ICON_TYPE_DELETE}
          onClick={handleRemove}
          accessibilityLabel="Delete Share"
        />
      </div>
    </div>
  );
}

ChipBase.propTypes = propTypes;
ChipBase.defaultProps = defaultProps;

export default ChipBase;
