import React from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';

import PresenceAvatar from '@ciscospark/react-container-presence-avatar';

import styles from './list-item.css';

const propTypes = {
  displayName: PropTypes.string,
  emailAddress: PropTypes.string,
  id: PropTypes.string,
  isExternal: PropTypes.bool,
  isPending: PropTypes.bool,
  onClick: PropTypes.func
};

const defaultProps = {
  emailAddress: '',
  id: '',
  displayName: '',
  isExternal: false,
  isPending: false,
  onClick: () => {}
};

function Person({
  displayName,
  emailAddress,
  id,
  isExternal,
  isPending,
  onClick
}) {
  function handleAction() {
    if (!isPending) {
      onClick();
    }
  }

  function handleClick() {
    handleAction();
  }

  function handleKeyPress(e) {
    if (e.key === 'Enter') {
      handleAction();
    }
  }

  const detailName = emailAddress.length ? `${displayName} (${emailAddress})` : `${displayName}`;

  return (
    <div
      className={classNames(
        'ciscospark-people-list-item',
        styles.item,
        {
          [styles.external]: isExternal,
          'ciscospark-people-list-item-external': isExternal,
          [styles.pending]: isPending,
          'ciscospark-people-list-item-pending': isPending
        }
      )}
      onClick={handleClick}
      onKeyPress={handleKeyPress}
      role="button"
      tabIndex="0"
      title={detailName}
    >
      <div className={classNames('ciscospark-people-list-avatar', styles.avatar)}>
        <PresenceAvatar avatarId={id} name={displayName} size={28} />
      </div>
      <div className={classNames('ciscospark-people-list-name', styles.name)}>
        {displayName}
      </div>
    </div>
  );
}

Person.propTypes = propTypes;
Person.defaultProps = defaultProps;

export default Person;
