import {createSelector} from 'reselect';

const getActivities = (state) => state.conversation.get(`activities`);
const getParticipants = (state) => state.conversation.get(`participants`);

export const getMostRecentReadReceipts = createSelector(
  [getActivities, getParticipants],
  (activities, participants) => {
    const activity = activities.last();
    return participants.toJS().filter((participant) =>
      participant.roomProperties && participant.roomProperties.lastSeenActivityUUID === activity.id
    );
  }
);
