import React, {PropTypes} from 'react';
import {connect} from 'react-redux';
import {bindActionCreators} from 'redux';

import {someAction} from './actions';

export function ExampleWidget(props) {
  const {
    exampleWidget
  } = props;
  return (
    <div>{exampleWidget.someObject}</div>
  );
}

const injectedProps = {
  exampleWidget: PropTypes.object
};

ExampleWidget.propTypes = {
  ...injectedProps
};


function mapStateToProps(state) {
  const {
    exampleWidget
  } = state;
  return {
    exampleWidget
  };
}

export default connect(
  mapStateToProps,
  (dispatch) => bindActionCreators({
    someAction
  }, dispatch)
)(ExampleWidget);
