import {fromJS} from 'immutable';

import {createRootReducer} from '@ciscospark/widget-base';
import {default as userReducer} from '@ciscospark/redux-module-user';
import {default as conversationReducer} from '@ciscospark/redux-module-conversation';

import {
  SOME_ACTION
} from './actions';

export const initialState = fromJS({
  someObject: `Some Text`
});

export function reducer(state = initialState, action) {
  switch (action.type) {
  case SOME_ACTION:
    return state.set(`someObject`, action.payload);

  default:
    return state;
  }
}

export default createRootReducer({
  exampleWidget: reducer,
  conversationReducer,
  userReducer
});
