import React from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';

import Button from '@ciscospark/react-component-button';
import Timer from '@ciscospark/react-component-timer';

import styles from './styles.css';

function JoinCallButton({callStartTime, onJoinClick}) {
  return (
    <div className={classNames('ciscospark-join-call', styles.join)}>
      <Button
        accessibilityLabel="Join Call"
        buttonClassName={classNames('ciscospark-join-call-button', styles.joinButton)}
        label="Join"
        onClick={onJoinClick}
      />
      <div className={classNames('ciscospark-join-call-duration', styles.duration)}>
        <Timer startTime={callStartTime} />
      </div>
    </div>
  );
}

JoinCallButton.propTypes = {
  callStartTime: PropTypes.number.isRequired,
  onJoinClick: PropTypes.func.isRequired
};

export default JoinCallButton;
