import constructSparkWidget from '@ciscospark/widget-base';

import {default as reducers} from './reducer';
import ConnectedRecents, {ownPropTypes} from './container';
import enTranslationMessages from './translations/en';

export {reducers};

export default constructSparkWidget(
  `recents`,
  ConnectedRecents,
  {
    reducers,
    metrics: true,
    intl: {
      messages: enTranslationMessages
    },
    propTypes: ownPropTypes
  }
);
