import React from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';

import {Spinner} from '@collab-ui/react';

import styles from './styles.css';

const propTypes = {
  loadingMessage: PropTypes.string
};

const defaultProps = {
  loadingMessage: ''
};

function LoadingScreen({loadingMessage}) {
  return (
    <div className={classNames('webex-teams-loading', styles.loading)}>
      <div className={classNames('webex-teams-spark-logo', styles.logo)} />
      {loadingMessage}
      <div className={classNames('webex-teams-spinner-container')}>
        <Spinner />
      </div>
    </div>
  );
}

LoadingScreen.propTypes = propTypes;
LoadingScreen.defaultProps = defaultProps;

export default LoadingScreen;
